function ExtCSFig(num)% ExtCSFig -- callback implementing functions of ExtCSDemoglobal EXTCSFIGNUMExtCSInitif 1 <= num & num <= 17 ,    nihil = ~exist('EXTCSFIGNUM');    if ~nihil,        empty = isempty(EXTCSFIGNUM);    else        empty = 1;    end    if ~empty,        nil   = EXTCSFIGNUM == 0;    else        nil   = 1;    end    if nil,        ExtCSInit    end    figname = sprintf('GenFig%g',num);    clc; home; help(figname);    eval(figname);    RegisterPlot(figname,'{R}');    drawnow;    EXTCSFIGNUM = num;elseif num==19,   % change default settings    clc; home    disp('There Are No Defaults to CHANGE');elseif num==20,   %run all figures    ExtCSInit    for num=1:17,        figname = sprintf('aifig%g',num);        eval(figname);        RegisterPlot(figname,'{R}');        clc; home; help(figname);        drawnow;        EXTCSFIGNUM = num;    endelse    disp('Bizarre argument in ExtCSFig')    disp(sprintf('num =%g',num))end    %% Copyright (c) 2006. Victoria Stodden and David Donoho%  %% Part of SparseLab Version:100% Created Tuesday March 28, 2006% This is Copyrighted Material% For Copying permissions see COPYING.m% Comments? e-mail sparselab@stanford.edu%