function HDCPNPDFig(num)% HDCPNPDFig -- callback implementing functions of HDCPNPDDemoglobal HDCPNPDFIGNUMHDCPNPDInitif 1 <= num & num <= 11 ,    nihil = ~exist('HDCPNPDFIGNUM');    if ~nihil,        empty = isempty(HDCPNPDFIGNUM);    else        empty = 1;    end    if ~empty,        nil   = HDCPNPDFIGNUM == 0;    else        nil   = 1;    end    if nil,        HDCPNPDInit    end    figname = sprintf('HDCPNPDFig%g',num);    clc; home; help(figname);    eval(figname);    RegisterPlot(figname,'{R}');    drawnow;    HDCPNPDFIGNUM = num;elseif num==19,   % change default settings    clc; home    disp('There Are No Defaults to CHANGE');elseif num==20,   %run all figures    HDCPNPDInit    for num=1:11,        figname = sprintf('HDCPNPDfig%g',num);        eval(figname);        RegisterPlot(figname,'{R}');        clc; home; help(figname);        drawnow;        HDCPNPDFIGNUM = num;    endelse    disp('Bizarre argument in HDCPNPDFig')    disp(sprintf('num =%g',num))end    %% Copyright (c) 2006. Victoria Stodden and David Donoho%  %% Part of SparseLab Version:100% Created Tuesday March 28, 2006% This is Copyrighted Material% For Copying permissions see COPYING.m% Comments? e-mail sparselab@stanford.edu%