function NRPSHDFig(num)% NRPSHDFig -- callback implementing functions of NRPSHDDemoglobal NRPSHDFIGNUMNRPSHDInitif 1 <= num & num <= 6 ,    nihil = ~exist('NRPSHDFIGNUM');    if ~nihil,        empty = isempty(NRPSHDFIGNUM);    else        empty = 1;    end    if ~empty,        nil   = NRPSHDFIGNUM == 0;    else        nil   = 1;    end    if nil,        NRPSHDInit    end    figname = sprintf('NRPSHDFig%g',num);    clc; home; help(figname);    eval(figname);    RegisterPlot(figname,'{R}');    drawnow;    NRPSHDFIGNUM = num;elseif num==19,   % change default settings    clc; home    disp('There Are No Defaults to CHANGE');elseif num==20,   %run all figures    NRPSHDInit    for num=1:6,        figname = sprintf('aifig%g',num);        eval(figname);        RegisterPlot(figname,'{R}');        clc; home; help(figname);        drawnow;        NRPSHDFIGNUM = num;    endelse    disp('Bizarre argument in NRPSHDFig')    disp(sprintf('num =%g',num))end    %% Copyright (c) 2006. Victoria Stodden and David Donoho%  %% Part of SparseLab Version:100% Created Tuesday March 28, 2006% This is Copyrighted Material% For Copying permissions see COPYING.m% Comments? e-mail sparselab@stanford.edu%